svg.script:

// Game state
let gameState = {
  business: { level: 1, points: 0 },
  coding: { level: 1, points: 0 },
  hacking: { level: 1, points: 0 },
  seo: { level: 1, points: 0 },
  design: { level: 1, points: 0 },
  totalPoints: 0,
  clicks: 0,
  level: 1
};

// Load saved game state
function loadGameState() {
  try {
    const saved = localStorage.getItem('githubSkillsGame');
    if (saved) {
      gameState = JSON.parse(saved);
      updateDisplay();
    }
  } catch(e) {
    // If there's an error, start fresh
  }
}

// Save game state
function saveGameState() {
  try {
    localStorage.setItem('githubSkillsGame', JSON.stringify(gameState));
  } catch(e) {
    // Storage might be unavailable
  }
}

// Increment a skill
function incrementSkill(skill) {
  // Increase skill points
  gameState[skill].points += Math.floor(Math.random() * 3) + 1;
  gameState.clicks++;

  // Check for level up (every 10 points)
  if (gameState[skill].points >= gameState[skill].level * 10) {
    gameState[skill].level++;
    gameState[skill].points = 0;

    // Show level up effect
    showLevelUp(skill);
  }

  // Update total points and overall level
  gameState.totalPoints++;
  gameState.level = Math.floor(gameState.totalPoints / 50) + 1;

  // Update display
  updateDisplay();

  // Save game
  saveGameState();

  // Show click effect
  showClickEffect(event);
}

// Update the display
function updateDisplay() {
  // Update skill levels
  document.getElementById('business-level').textContent = gameState.business.level;
  document.getElementById('coding-level').textContent = gameState.coding.level;
  document.getElementById('hacking-level').textContent = gameState.hacking.level;
  document.getElementById('seo-level').textContent = gameState.seo.level;
  document.getElementById('design-level').textContent = gameState.design.level;

  // Update progress bars
  updateProgressBar('business', gameState.business.points, gameState.business.level * 10);
  updateProgressBar('coding', gameState.coding.points, gameState.coding.level * 10);
  updateProgressBar('hacking', gameState.hacking.points, gameState.hacking.level * 10);
  updateProgressBar('seo', gameState.seo.points, gameState.seo.level * 10);
  updateProgressBar('design', gameState.design.points, gameState.design.level * 10);

  // Update stats
  document.getElementById('total-points').textContent = gameState.totalPoints;
  document.getElementById('level').textContent = gameState.level;
  document.getElementById('clicks').textContent = gameState.clicks;
}

// Update progress bar width
function updateProgressBar(skill, points, maxPoints) {
  const bar = document.getElementById(skill + '-bar');
  const width = (points / maxPoints) * 70;
  bar.setAttribute('width', Math.max(10, width));
}

// Show click effect
function showClickEffect(evt) {
  // Create a new click effect
  const effect = document.getElementById('click-effect-template').cloneNode(true);
  effect.setAttribute('id', 'click-effect-' + Date.now());
  effect.setAttribute('x', evt.clientX ? evt.clientX - 15 : 100);
  effect.setAttribute('y', evt.clientY ? evt.clientY - 15 : 100);
  effect.setAttribute('display', 'inline');

  // Add to animations container
  const container = document.getElementById('click-animations');
  container.appendChild(effect);

  // Remove after animation completes
  setTimeout(() => {
    if (effect.parentNode) {
      container.removeChild(effect);
    }
  }, 600);

  // Trigger animation
  const circle = effect.querySelector('circle');
  if (circle) {
    circle.beginElement();
  }
}

// Show level up effect
function showLevelUp(skill) {
  const skillElement = document.getElementById(skill + '-skill');
  const rect = skillElement.querySelector('rect');

  // Flash animation
  const originalFill = rect.getAttribute('fill');
  rect.setAttribute('fill', '#58a6ff');

  setTimeout(() => {
    rect.setAttribute('fill', originalFill);
  }, 300);

  setTimeout(() => {
    rect.setAttribute('fill', '#58a6ff');
  }, 500);

  setTimeout(() => {
    rect.setAttribute('fill', originalFill);
  }, 700);
}

// Reset game
function resetGame() {
  if (confirm('Reset all progress?')) {
    gameState = {
      business: { level: 1, points: 0 },
      coding: { level: 1, points: 0 },
      hacking: { level: 1, points: 0 },
      seo: { level: 1, points: 0 },
      design: { level: 1, points: 0 },
      totalPoints: 0,
      clicks: 0,
      level: 1
    };

    updateDisplay();
    saveGameState();
  }
}

// Add reset button functionality
document.addEventListener('DOMContentLoaded', function() {
  // Add a reset button (hidden but accessible via context menu)
  const svg = document.querySelector('svg');
  svg.addEventListener('contextmenu', function(e) {
    e.preventDefault();
    resetGame();
  });

  // Load saved game on startup
  loadGameState();
});

// Initialize game
loadGameState();
